int find_marker(string str)
{
    for(int i = 3; i < str.size(); i++)
    {
        char first = str[i-3];
        char second = str[i-2];
        char third = str[i-1];
        char last = str[i];

        if (first == second)
        {
            continue;
        }
        if (first == third)
        {
            continue;
        }
        if (first == last)
        {
            continue;
        }
        if (second == third)
        {
            i += 1;
            continue;
        }
        if (second == last)
        {
            i += 1;
            continue;
        }
        if (third == last)
        {
            i += 2;
            continue;
        }

        return i + 1;
    }
    return -1;
}

int main()
{
    cout << find_marker("bvwbjplbgvbhsrlpgdmjqwftvncz") << '\n';
    cout << find_marker("nppdvjthqldpwncqszvftbrmjlhg") << '\n';
    cout << find_marker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg") << '\n';
    cout << find_marker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw") << '\n';
    const string input = "";
    cout << find_marker(input) << '\n';
    return 0;
}